mapper.insert:

  select sys_guid() from dual

insert into RUICAI.EDUCATION_TEACHER (ID, USER_ID, TEACHER_TYPE,
  TEACHER_SPECIALITY, TEACHER_SEX, TEACHER_NAME, 
  TEACHER_PHONE, TEACHER_INTRODUCTION, TEACHER_PICTURE
  )
values (#{id,jdbcType=VARCHAR}, #{userId,jdbcType=VARCHAR}, #{teacherType,jdbcType=VARCHAR},
  #{teacherSpeciality,jdbcType=VARCHAR}, #{teacherSex,jdbcType=VARCHAR}, #{teacherName,jdbcType=VARCHAR}, 
  #{teacherPhone,jdbcType=VARCHAR}, #{teacherIntroduction,jdbcType=VARCHAR}, #{teacherPicture,jdbcType=VARCHAR}
  )

mapper.select:
select count(id) from EDUCATION_TEACHER

  
  id = #{id}
  
  
   and TEACHER_TYPE = #{teacherType}
  
  
    and TEACHER_NAME  like  '%${teacherName}%'

SELECT * FROM ( SELECT a1.*, ROWNUM rn FROM (SELECT et."ID", et.USER_ID, et.TEACHER_PHONE, et.TEACHER_INTRODUCTION, et.teacher_picture, et.TEACHER_NAME, et.TEACHER_SPECIALITY, et.TEACHER_SEX, et.TEACHER_TYPE, sd.DICTIONARY_NAME as TEACHER_SEX_VIEW, sd1.DICTIONARY_NAME as TEACHER_SPECIALITY_VIEW, sd2.DICTIONARY_NAME as TEACHER_TYPE_VIEW FROM ( ( ( EDUCATION_TEACHER et JOIN SYSTEM_DICTIONARY sd ON ET.TEACHER_SEX = SD."ID" ) JOIN SYSTEM_DICTIONARY sd1 ON ET.TEACHER_SPECIALITY = SD1."ID" ) JOIN SYSTEM_DICTIONARY sd2 ON ET.TEACHER_TYPE = SD2."ID" ) where ROWNUM < #{endNum}
and id = #{id}
and TEACHER_TYPE = #{teacherType}
and TEACHER_NAME like '%${teacherName}%'
) a1 ) WHERE rn > #{startNum}
