mapper.insert:
insert into RUICAI.EDUCATION_WORK (ID, TEACHER_ID, WORK_TYPE, 
  WORK_URL, WORK_UPLOAD_TIME, WORK_REMARK
  )
values (#{id,jdbcType=VARCHAR}, #{teacherId,jdbcType=VARCHAR}, #{workType,jdbcType=VARCHAR}, 
  #{workUrl,jdbcType=VARCHAR}, #{workUploadTime,jdbcType=TIMESTAMP}, #{workRemark,jdbcType=VARCHAR}
  )

mapper.select:
SELECT * FROM ( SELECT A .*, ROWNUM rn FROM ( SELECT EW."ID", EW.TEACHER_ID, EW.WORK_TYPE, EW.WORK_UPLOAD_TIME, EW.WORK_URL, ET.TEACHER_NAME AS TEACHER_VIEW FROM( EDUCATION_WORK EW JOIN SYSTEM_DICTIONARY sd ON EW.WORK_TYPE = SD."ID") JOIN EDUCATION_TEACHER et ON EW.TEACHER_ID = ET."ID" WHERE EW.WORK_TYPE = ( SELECT "ID" FROM SYSTEM_DICTIONARY WHERE DICTIONARY_VALUE = #{workType} ) AND ROWNUM < #{endNum}
AND TEACHER_ID = #{teacherId}
AND WORK_UPLOAD_TIME > to_date(#{workUploadTime},'yyyy-mm-dd hh24:mi:ss')
) A ) WHERE rn > #{startNum}
